
public class Preg1 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int valor1 = 800;
		int valor2 = 900;

		System.out.println("la suma de los dos valores es " + (valor1 + valor2));
		System.out.println("la resta de los dos valores es " + (valor1 - valor2));
		System.out.println("la multiplicacion de los dos valores es " + (valor1 * valor2));
		System.out.println("la division de los dos valores es " + (valor1 / valor2));
		System.out.println("la resta de la division de los dos valores es " + (valor1 % valor2));
	}

}
