
public class Preg2 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int N = 1000;
		double A = 500.5;
		char C = 'b';

		System.out.println("Variable N es " + N);
		System.out.println("Variable A es " + A);
		System.out.println("Variable C es " + C);

		System.out.println("la suma de los valores N y A es " + (N + A));

		System.out.println("La resta de los valoes A y N es " + (A - N));

		System.out.println("Valor numérico del carácter " + C + " = " + (int) C);
	}

}
