
public class Preg3 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int Y = 5;
		int X = 6;
		double N = 7;
		double M = 8;

		System.out.println("\"Aqui tiene la iformacion que ser√° usada " + Y + X + N + M);
		System.out.println("suma " + (Y + X));
		System.out.println("resta " + (Y - X));
		System.out.println("division " + (Y / X));
		System.out.println("multi " + (Y * X));
		System.out.println("resto " + (Y % X));
		System.out.println("suma " + (N + M));
		System.out.println("resta " + (N - M));
		System.out.println("multi " + (N * M));
		System.out.println("divi " + (N / M));
		System.out.println("resto " + (N % M));
		System.out.println("la suma de los valores X y N es " + (X + N));
		System.out.println("El coeciente de los valores Y y M es " + (Y / M));
		System.out.println("El resto de Y y M es " + (Y % M));
		System.out.println("El doble de los valores es " + 2 * (Y) + 2 * (X) + (N) + 2 * (M));
		System.out.println("La suma de todas las variables es " + (Y + X + M + N));
		System.out.println("La suma de todas las variables es " + (Y * X * M * N));
	}

}
