
public class Preg4 {

	public static void main(String[] args) {
		// TODO Auto-generated method stub
		int N = 500;
		System.out.println("El valor incrementado de la variable N es " + (N + 77));
		System.out.println("El valor decrementado de la variable N es " + (N - 3));
		System.out.println("El valor duplicado del valor N es " + (N * 2));
	}

}
